import React from "react";
import { ApolloProvider } from "@apollo/client";
import { client } from "./apollo/client";

import CompteList from "./components/CompteList";
import CreateCompte from "./components/CreateCompte";
import TransactionForm from "./components/TransactionForm";
import TransactionList from "./components/TransactionList";

function App() {
  return (
    <ApolloProvider client={client}>
      <div className="min-h-screen p-6 bg-gradient-to-br from-slate-50 to-teal-50">
        <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-4 gap-6">
          {/* Sidebar */}
          <aside className="md:col-span-1 bg-slate-900 text-slate-100 rounded-xl p-6 shadow-lg">
            <h2 className="text-2xl font-bold mb-4">Actions</h2>
            <p className="text-sm text-slate-300 mb-4">Créer compte ou effectuer une transaction</p>

            <div className="space-y-6">
              <div className="bg-white rounded-lg p-4">
                <CreateCompte />
              </div>

              <div className="bg-white rounded-lg p-4">
                <TransactionForm />
              </div>
            </div>
          </aside>

          {/* Main content */}
          <main className="md:col-span-3 space-y-6">
            <header className="p-4 rounded-lg bg-white shadow-sm">
              <h1 className="text-3xl font-extrabold text-teal-700">Gestion des Comptes Bancaires</h1>
              <p className="text-sm text-teal-600 mt-1">Tableau de bord simplifié — aperçu des comptes et des transactions</p>
            </header>

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <div className="">
                <CompteList />
              </div>
              <div className="">
                <TransactionList />
              </div>
            </div>
          </main>
        </div>
      </div>
    </ApolloProvider>
  );
}

export default App;
