import { useMutation } from '@apollo/client';
import { SAVE_COMPTE } from '../graphql/mutations';
import { useState } from 'react';

export default function CreateCompte() {
  const [solde, setSolde] = useState('');
  const [type, setType] = useState('COURANT');

  const [saveCompte] = useMutation(SAVE_COMPTE);

  const handleSubmit = (e) => {
    e.preventDefault();

    saveCompte({
      variables: {
        compte: { solde: parseFloat(solde), type }
      }
    });

    setSolde('');
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white shadow-sm rounded-lg p-6">
      <h2 className="text-xl font-semibold mb-4">Créer un compte</h2>

      <label className="block text-sm text-gray-600 mb-1">Solde</label>
      <input
        type="number"
        placeholder="Solde"
        className="border p-2 mb-3 w-full rounded focus:outline-none focus:ring-2 focus:ring-blue-200"
        value={solde}
        onChange={(e) => setSolde(e.target.value)}
      />

      <label className="block text-sm text-gray-600 mb-1">Type de compte</label>
      <select
        className="border p-2 mb-4 w-full rounded focus:outline-none"
        value={type}
        onChange={(e) => setType(e.target.value)}
      >
        <option value="COURANT">Courant</option>
        <option value="EPARGNE">Épargne</option>
      </select>

      <div className="flex justify-end">
        <button className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded shadow-sm">
          Ajouter
        </button>
      </div>
    </form>
  );
}
