import { useState } from "react";
import { useMutation } from "@apollo/client";
import { ADD_TRANSACTION } from "../graphql/mutations";
import { GET_ALL_TRANSACTIONS, GET_ALL_COMPTES } from "../graphql/queries";

export default function TransactionForm() {
  const [compteId, setCompteId] = useState("");
  const [montant, setMontant] = useState("");
  const [type, setType] = useState("DEPOT");

  const [status, setStatus] = useState({ loading: false, error: null, success: null });

  const [addTransaction] = useMutation(ADD_TRANSACTION, {
    onCompleted() { setStatus({ loading: false, error: null, success: 'Transaction ajoutée.' }); },
    onError(err) { setStatus({ loading: false, error: err.message || 'Erreur transaction', success: null }); }
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    setStatus({ loading: true, error: null, success: null });

    addTransaction({
      variables: {
        transactionRequest: {
          compteId: parseInt(compteId),
          montant: parseFloat(montant),
          type,
        },
      },
      refetchQueries: [{ query: GET_ALL_TRANSACTIONS }, { query: GET_ALL_COMPTES }]
    });

    setMontant("");
  };

  return (
    <form onSubmit={handleSubmit} className="">
      <h2 className="font-semibold text-lg mb-3 text-slate-800">Effectuer une transaction</h2>

      <label className="block text-xs text-slate-500 mb-1">ID compte</label>
      <input
        className="border p-2 mb-3 w-full rounded focus:outline-none focus:ring-2 focus:ring-amber-100"
        placeholder="ID Compte"
        value={compteId}
        onChange={(e) => setCompteId(e.target.value)}
      />

      <label className="block text-xs text-slate-500 mb-1">Montant</label>
      <input
        className="border p-2 mb-3 w-full rounded"
        placeholder="Montant"
        type="number"
        value={montant}
        onChange={(e) => setMontant(e.target.value)}
      />

      <label className="block text-xs text-slate-500 mb-1">Type</label>
      <select
        className="border p-2 mb-4 w-full rounded"
        value={type}
        onChange={(e) => setType(e.target.value)}
      >
        <option value="DEPOT">Dépôt</option>
        <option value="RETRAIT">Retrait</option>
      </select>

      <div className="flex justify-end">
        <button className="bg-amber-500 hover:bg-amber-600 text-white px-4 py-2 rounded shadow-sm">
          Valider
        </button>
      </div>
    </form>
  );
}
